import React, { Component } from 'react'



export default class App extends Component {
  constructor() {
    super();
    this.state = {
    clicksBtn1: 0,
    clicksBtn2: 0,
    clicksBtn3: 0,
  };
    this.handleButtonOne = this.handleButtonOne.bind(this);
    this.handleButton2 = this.handleButton2.bind(this);
    this.handleButton3 = this.handleButton3.bind(this);
  }

  handleButtonOne() {
    const { clicksBtnOne } = this.state;
    this.setState((prevState) => ({
      clicksBtnOne: prevState.clicksBtnOne + 1,
    }), () => {
      console.log(`Botão 1 ${this.getButtonColor(clicksBtnOne)}`);
    });
  }

  handleButtonTwo() {
    const { clicksBtnTwo } = this.state;
    this.setState((prevState) => ({
      clicksBtnTwo: prevState.clicksBtnTwo + 1,
    }), () => {
      console.log(`Botão 2 ${this.getButtonColor(clicksBtnTwo)}`);
    });
  }

  handleButtonThree() {
    const { clicksBtnThree } = this.state;
    this.setState((prevState) => ({
      clicksBtnThree: prevState.clicksBtnThree + 1,
    }), () => {
      console.log(`Botão 3 ${this.getButtonColor(clicksBtnThree)}`);
    });
  }

  getButtonColor(num) {
    return num % 2 === 0 ? 'green' : 'white';
  }

  render() {
    const { clicksBtn1, clicksBtn2, clicksBtn3 } = this.state;
    return (
      <div>
        <button
        type="button"
        onClick={ this.handleButton1 }
        style={ { backgroundColor: this.getButtonColor(clicksBtn1) }  }
      >
        {`Cliques botão 1: ${clicksBtn1}`}
      </button>
      <button
        type="button"
        onClick={ this.handleButton2 }
        style={ { backgroundColor: this.getButtonColor(clicksBtn2) }  }
      >
        {`Cliques botão 2: ${clicksBtn2}`}
      </button>
      <button
        type="button"
        onClick={ this.handleButton3 }
        style={ { backgroundColor: this.getButtonColor(clicksBtn3) }  }
      >
        {`Cliques botão 3: ${clicksBtn3}`}
      </button>
      </div>
    );
  }
}
