import React, { Component } from 'react'



export default class App extends Component {
  constructor() {
    super();
    this.state = {
    clicksBtn1: 0,
    clicksBtn2: 0,
    clicksBtn3: 0,
  };
    this.handleButton1 = this.handleButton1.bind(this);
    this.handleButton2 = this.handleButton2.bind(this);
    this.handleButton3 = this.handleButton3.bind(this);
  }

  handleButton1() {
    const { clicksBtn1 } = this.state;
    this.setState((prevState) => ({
      clicksBtn1: prevState.clicksBtn1 + 1,
    })), () => {}
  }

  handleButton2() {
    const { clicksBtn2 } = this.state;
    this.setState((prevState) => ({
      clicksBtn2: prevState.clicksBtn2 + 1,
    }));
  }

  handleButton3() {
    const { clicksBtn3 } = this.state;
    this.setState((prevState) => ({
      clicksBtn3: prevState.clicksBtn3 + 1,
    }));
  }

  getButtonColor(num) {
    return num % 2 === 0 ? 'green' : 'white';
  }

  render() {
    const { clicksBtn1, clicksBtn2, clicksBtn3 } = this.state;
    return (
      <div>
        <button
        type="button"
        onClick={ this.handleButton1 }
        style={ { backgroundColor: this.getButtonColor(clicksBtn1) }  }
      >
        {`Cliques botão 1: ${clicksBtn1}`}
      </button>
      <button
        type="button"
        onClick={ this.handleButton2 }
        style={ { backgroundColor: this.getButtonColor(clicksBtn2) }  }
      >
        {`Cliques botão 2: ${clicksBtn2}`}
      </button>
      <button
        type="button"
        onClick={ this.handleButton3 }
        style={ { backgroundColor: this.getButtonColor(clicksBtn3) }  }
      >
        {`Cliques botão 3: ${clicksBtn3}`}
      </button>
      </div>
    );
  }
}
